import { useState } from "react";

class MessageParser {
  constructor(actionProvider) {
    this.actionProvider = actionProvider;
  }

  parse(message) {
    const [hasTimePassed, setHasTimePassed] =
      useState(false);
    if (hasTimePassed === false) {
      setHasTimePassed(true);
      this.actionProvider.greet();
    }
    // setTimeout(
