import "./ChatIcon.css";

function ChatIcon() {
  return (
    <div
      className="chat-icon"
      onClick={toggleChat}
    ></div>
  );
}

function toggleChat() {
  const chatBox = document.querySelector(".rsc");
  chatBox.classList.toggle("rsc--active");
}

export default ChatIcon;
