import { useState } from "react";
import useToggle from "../../hooks/useToggle";
import "./Nav.css";

function Nav() {
  const [isMenuActive, toggleIsMenuActive] =
    useToggle(false);

  return (
    <nav
      className={isMenuActive && "nav--active"}
    >
      <MenuIcon onclick={toggleIsMenuActive} />
    </nav>
  );
}

function MenuIcon({ toggleIsMenuActive }) {
  return (
    <div
      className="nav__menu-icon"
      onClick={toggleIsMenuActive}
    >
      <span className="nav__menu-icon--top"></span>
      <span className="nav__menu-icon--mid"></span>
      <span className="nav__menu-icon--bot"></span>
    </div>
  );
}

export default Nav;
